import React, { Component } from 'react'
import Sound from 'react-sound';

class SoundEffect extends Component {
  constructor(props) {
    super(props);

  }

  getInitialState() {
    return({hidden : "hidden"});
  }

  componentWillMount() {
    var that = this;
    setTimeout(function() {
        that.show();
    }, that.props.delay);
  }

  show() {
    this.setState({hidden : ""});
  }

  render() {
    return <Sound 
        url={this.props.url}
        playStatus={Sound.status.PLAYING}
        loop={false}
      />
  }
}


export default SoundEffect;
