import React, {Component} from 'react';
import './App.css';
import PanelController from './PanelController';

class App extends Component {
  configuration = {
    title: "Boku No Hero Academia",
    delayConfig: 500,
    chapterNum: 3,
    pagesConfig: [
      {id: 0, img: "https://i10.mangareader.net/boku-no-hero-academia/3/boku-no-hero-academia-5262013.jpg"},
      {id: 1, img: "https://i8.mangareader.net/boku-no-hero-academia/3/boku-no-hero-academia-5262015.jpg"},
      {id: 2, img: "https://i4.mangareader.net/boku-no-hero-academia/3/boku-no-hero-academia-5262017.jpg"},
      {id: 3, img: "https://i8.mangareader.net/boku-no-hero-academia/3/boku-no-hero-academia-5262019.jpg"},
      {id: 4, img: "https://i3.mangareader.net/boku-no-hero-academia/3/boku-no-hero-academia-5262021.jpg"},
      {id: 5, img: "https://i3.mangareader.net/boku-no-hero-academia/3/boku-no-hero-academia-5262023.jpg"},
      {id: 6, img: "https://i3.mangareader.net/boku-no-hero-academia/3/boku-no-hero-academia-5262025.jpg"},
      {id: 7, img: "https://i1.mangareader.net/boku-no-hero-academia/3/boku-no-hero-academia-5262027.jpg"},
      {id: 8, img: "https://i7.mangareader.net/boku-no-hero-academia/3/boku-no-hero-academia-5262029.jpg"},
      {id: 9, img: "https://i1.mangareader.net/boku-no-hero-academia/3/boku-no-hero-academia-5262031.jpg"},
      {id: 10, img: "https://i7.mangareader.net/boku-no-hero-academia/3/boku-no-hero-academia-5262033.jpg"},
      {id: 11, img: "https://i7.mangareader.net/boku-no-hero-academia/3/boku-no-hero-academia-5262035.jpg"},
      {id: 12, img: "https://i3.mangareader.net/boku-no-hero-academia/3/boku-no-hero-academia-5262037.jpg"},
      {id: 13, img: "https://i9.mangareader.net/boku-no-hero-academia/3/boku-no-hero-academia-5262039.jpg"}
    ]
  }

  constructor(props) {
    super(props);

    if (1 in this.configuration.pagesConfig) {
      this.setCurrentPageConfiguration(1);
    }

    this.onPageClick = this.onPageClick.bind(this);
    this.onKeyUp = this.onKeyUp.bind(this);
    this.disableEvents = this.disableEvents.bind(this);
    this.myRef = React.createRef();
  }


  setCurrentPageConfiguration(index) {
      this.state = {
        index: 1
      } 
  }

  onPageClick(e) {
    var posX = e.clientX;

    var mangaContainerDiv = document.getElementById("MangaContainer");

    var MangaPanelX = posX - mangaContainerDiv.offsetLeft;
    var halfwayPointX = mangaContainerDiv.offsetWidth / 2.0;

    if (MangaPanelX < halfwayPointX) {
      this.handlePageBackwardEvent();
      console.log("page backwards");
    }
    else {
      this.handlePageForwardEvent();
      console.log("page forward");
    }
    // console.log("item clicked on " + MangaPanelX + ", " + MangaPanelY);
  }

  updateState(index, img, soundFX, backgroundFX) {
    this.setState({
      index: index,
      img: img,
      soundFX: soundFX,
      backgroundFX: backgroundFX
    });
  }

  disableEvents() {
    this.setState({
      canAnimate: false
    });
  }

  handlePageForwardEvent() {
    console.log("Handling Page Forward Event");

    var nextPageIndex = this.state["index"] + 1;

    if (nextPageIndex in this.configuration.pagesConfig) {
      this.updateState(nextPageIndex, 
        this.configuration.pagesConfig[nextPageIndex].img, 
        this.configuration.pagesConfig[nextPageIndex].soundFX,
        this.configuration.pagesConfig[nextPageIndex].backgroundFX);

      this.disableEvents();
      setTimeout(function() { this.disableEvents(); }, this.configuration.delayConfig);
    }
  }

  handlePageBackwardEvent() {
    console.log("Handling Page Backward Event");

    var nextPageIndex = this.state["index"] - 1;
    if (nextPageIndex in this.configuration.pagesConfig) {
      this.updateState(nextPageIndex, 
        this.configuration.pagesConfig[nextPageIndex].img, 
        this.configuration.pagesConfig[nextPageIndex].soundFX,
        this.configuration.pagesConfig[nextPageIndex].backgroundFX);

      this.disableEvents();
      setTimeout(function() { this.disableEvents(); }, this.configuration.delayConfig);
    }
  }
  
  onKeyUp(e) {
    if (e.key === 'ArrowRight') {
      this.handlePageForwardEvent();
    } else if (e.key === 'ArrowLeft') {
      this.handlePageBackwardEvent();
    }
  }

  render() {
    return (
      <div className="App" onKeyUp={this.onKeyUp} tabIndex="0" >
        <header className="App-header">
          <div onClick={this.onPageClick} id="MangaContainer">
            <PanelController delayConfig={this.configuration.delayConfig} state={this.state} pagesConfig={this.configuration.pagesConfig}/>
          </div>
        </header>
      </div>
    )
  }
}

export default App;
