import React, {Component} from 'react';
import './App.css';
import PanelController from './PanelController';

class App extends Component {
  configuration = {
    title: "Boku No Hero Academia",
    chapterNum: 3,
    delayConfig: 500,
    minIndex: 0,
    maxIndex: 21,
    pagesConfig: [
      {id: 0, img: "https://raw.githubusercontent.com/expensivecow/ImageAndSoundRepo/master/Manga/BokuNoHero/3/003.jpg"},
      {id: 1, img: "https://raw.githubusercontent.com/expensivecow/ImageAndSoundRepo/master/Manga/BokuNoHero/3/004.jpg"},
      {id: 2, img: "https://raw.githubusercontent.com/expensivecow/ImageAndSoundRepo/master/Manga/BokuNoHero/3/005.jpg"},
      {id: 3, img: "https://raw.githubusercontent.com/expensivecow/ImageAndSoundRepo/master/Manga/BokuNoHero/3/006.jpg"},
      {id: 4, img: "https://raw.githubusercontent.com/expensivecow/ImageAndSoundRepo/master/Manga/BokuNoHero/3/007.jpg"},
      {id: 5, img: "https://raw.githubusercontent.com/expensivecow/ImageAndSoundRepo/master/Manga/BokuNoHero/3/008.jpg"},
      {id: 6, img: "https://raw.githubusercontent.com/expensivecow/ImageAndSoundRepo/master/Manga/BokuNoHero/3/009.jpg"},
      {id: 7, img: "https://raw.githubusercontent.com/expensivecow/ImageAndSoundRepo/master/Manga/BokuNoHero/3/010.jpg"},
      {id: 8, img: "https://raw.githubusercontent.com/expensivecow/ImageAndSoundRepo/master/Manga/BokuNoHero/3/011.jpg"},
      {id: 9, img: "https://raw.githubusercontent.com/expensivecow/ImageAndSoundRepo/master/Manga/BokuNoHero/3/012.jpg"},
      {id: 10, img: "https://raw.githubusercontent.com/expensivecow/ImageAndSoundRepo/master/Manga/BokuNoHero/3/013.jpg"},
      {id: 11, img: "https://raw.githubusercontent.com/expensivecow/ImageAndSoundRepo/master/Manga/BokuNoHero/3/014.jpg"},
      {id: 12, img: "https://raw.githubusercontent.com/expensivecow/ImageAndSoundRepo/master/Manga/BokuNoHero/3/015.jpg"},
      {id: 13, img: "https://raw.githubusercontent.com/expensivecow/ImageAndSoundRepo/master/Manga/BokuNoHero/3/016.jpg"},
      {id: 14, img: "https://raw.githubusercontent.com/expensivecow/ImageAndSoundRepo/master/Manga/BokuNoHero/3/017.jpg"},
      {id: 15, img: "https://raw.githubusercontent.com/expensivecow/ImageAndSoundRepo/master/Manga/BokuNoHero/3/018.jpg"},
      {id: 16, img: "https://raw.githubusercontent.com/expensivecow/ImageAndSoundRepo/master/Manga/BokuNoHero/3/019.jpg"},
      {id: 17, img: "https://raw.githubusercontent.com/expensivecow/ImageAndSoundRepo/master/Manga/BokuNoHero/3/020.jpg"},
      {id: 18, img: "https://raw.githubusercontent.com/expensivecow/ImageAndSoundRepo/master/Manga/BokuNoHero/3/021.jpg"},
      {id: 19, img: "https://raw.githubusercontent.com/expensivecow/ImageAndSoundRepo/master/Manga/BokuNoHero/3/022.jpg"},
      {id: 20, img: "https://raw.githubusercontent.com/expensivecow/ImageAndSoundRepo/master/Manga/BokuNoHero/3/023.jpg"},
      {id: 21, img: "https://raw.githubusercontent.com/expensivecow/ImageAndSoundRepo/master/Manga/BokuNoHero/3/024.jpg"}
    ],
    soundEffectConfig: [
      {id: 0, soundEffects: [{url: '', config: {}}, {url: '', config: {}}]},
      {id: 1, soundEffects: [{url: '', config: {}}]}
    ],
    backgroundMusicConfig: [
      {id: 0, music: {url: 'https://raw.githubusercontent.com/expensivecow/ImageAndSoundRepo/master/BackgroundMusic/BecomeAHero.mp3', config: {volume: 100, delay: 0}} },
      {id: 1, music: {url: '', config: {volume: 100, delay: 0}} }
    ]
  }

  constructor(props) {
    super(props);

    this.initializeState(0);

    this.onPageClick = this.onPageClick.bind(this);
    this.onKeyUp = this.onKeyUp.bind(this);
    this.updateCanActivateEvent = this.updateCanActivateEvent.bind(this);
    this.myRef = React.createRef();
  }


  initializeState(index) {
      this.state = {
        index: 0,
        canAnimate: true
      } 
  }

  onPageClick(e) {
    var posX = e.clientX;

    var mangaContainerDiv = document.getElementById("MangaContainer");

    var MangaPanelX = posX - mangaContainerDiv.offsetLeft;
    var halfwayPointX = mangaContainerDiv.offsetWidth / 2.0;

    if (MangaPanelX < halfwayPointX) {
      this.handlePageBackwardEvent();
      console.log("page backwards");
    }
    else {
      this.handlePageForwardEvent();
      console.log("page forward");
    }
    // console.log("item clicked on " + MangaPanelX + ", " + MangaPanelY);
  }

  updateState(index) {
    this.setState({
      index: index
    });
  }

  updateCanActivateEvent(flag) {
    this.setState({
      canAnimate: flag
    });
  }

  handlePageForwardEvent() {
    console.log("Handling Page Forward Event");

    var nextPageIndex = this.state["index"] + 1;

    if (nextPageIndex <= this.configuration.maxIndex && this.state['canAnimate']) {
      this.updateState(nextPageIndex);

      this.updateCanActivateEvent(false);
      setTimeout(() => {this.updateCanActivateEvent(true);}, this.configuration.delayConfig);
    }
    else {
      console.log('cannot animate');
    }
  }

  handlePageBackwardEvent() {
    console.log("Handling Page Backward Event");

    var nextPageIndex = this.state["index"] - 1;
    if (nextPageIndex > this.configuration.minIndex && this.state['canAnimate']) {
      this.updateState(nextPageIndex);

      this.updateCanActivateEvent(false);
      setTimeout(() => {this.updateCanActivateEvent(true);}, this.configuration.delayConfig);
    }
    else {
      console.log('cannot animate');
    }
  }
  
  onKeyUp(e) {
    if (e.key === 'ArrowRight') {
      this.handlePageForwardEvent();
    } else if (e.key === 'ArrowLeft') {
      this.handlePageBackwardEvent();
    }
  }

  render() {
    return (
      <div className="App" onKeyUp={this.onKeyUp} tabIndex="0" >
        <header className="App-header">
          <div onClick={this.onPageClick} id="MangaContainer">
            <PanelController delayConfig={this.configuration.delayConfig} state={this.state} pagesConfig={this.configuration.pagesConfig}/>
          </div>
        </header>
      </div>
    )
  }
}

export default App;
