import React, { useState, useEffect } from 'react'
import {useAudio} from 'react-use';

function SoundEffect(props) {
  var [audio, state, controls, ref] = useAudio({
    src: props.config.url,
    autoPlay: true,
  });

  var delay = 0;
  if (this.props.delay !== null) {
    delay = this.props.delay;
  }

  console.log('waiting ' + delay + ' seconds to launch sound effect')
  setTimeout(function(){
    //do what you need here
  }, delay);

  function render() {
    return <div>{audio}</div>
  }

  return render();
}

export default SoundEffect;
